package JavaBasicCoding;

public class DivideByZero {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * // what will be output when you divide a number by zero? // Number = Integer,
		 * Double,Float
		 * 
		 * Arithmethic exception will come only on Integer numnber
		 */
		
		
		System.out.println(9.0/0);
		System.out.println(12.33f/0);
		System.out.println(10/0.0);
		System.out.println(19.9999d/0);
		//System.out.println(0/0);
		System.out.println(0.0/0);
		System.out.println(0.0/0.0);
	}

}
